mapper.sql:
			from
			codeGroup a
	    WHERE 1=1

        	AND a.codeGroupDelNy = #{shDelNy}


        		AND a.codeGroupRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
				AND a.codeGroupModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            	AND a.codeGroupSeq = #{shValue}
            	AND a.codeGroupName LIKE CONCAT('%',#{shValue},'%')
            	AND a.codeGroupMemo LIKE CONCAT('%',#{shValue},'%')

mapper.select:
select codeGroupSeq ,codeGroupDelNy ,codeGroupUseNy ,codeGroupName ,codeGroupMemo ,codeGroupRegDate ,codeGroupModDate
from codeGroup
where 1=1 and codeGroupSeq = #{codeGroupSeq}

mapper.update:
update codeGroup
set
	codeGroupName = #{codeGroupName}
	,codeGroupMemo = #{codeGroupMemo}
	,codeGroupModDate = now()


WHERE 1=1
	and codeGroupSeq = #{codeGroupSeq}

mapper.delete:
delete from codeGroup

where 1=1
	and codeGroupSeq = #{codeGroupSeq}
