mapper.select:
select a.seq as codeSeq ,a.name as codeName ,a.memo as codeMemo ,a.delNy as codeDelNy ,a.regDatetime as codeRegDatetime ,a.modDatetime as codeModDatetime ,a.codeGroup_seq as codeGroupSeqF ,b.seq as codeGroupSeq ,b.name as codeGroupName from code a left join codeGroup b on a.codeGroup_seq = b.seq and a.delNy = 0 where 1=1
AND a.delNy = #{shDelNy}
AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd} AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
AND b.seq = #{shValue} AND b.name LIKE CONCAT('%',#{shValue},'%') AND a.seq = #{shValue} AND a.name LIKE CONCAT('%',#{shValue},'%') AND a.memo LIKE CONCAT('%',#{shValue},'%')

mapper.update:
update code
set
	name = #{codeName}
	,memo = #{codeMemo}
	,modDateTime = now()
where 1=1
	and seq = #{codeSeq}

mapper.delete:
delete from code

where 1=1
	and seq = #{codeSeq}
