mapper.sql:
		FROM code a
		left join codeGroup b 
			on a.codeGroup_codeGroupSeq = b.codeGroupSeq 
				and a.codeDelNy = 0
		where 1=1


		AND a.codeDelNy = #{shDelNy}


			AND a.codeRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.codeModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND b.codeGroupSeq = #{shValue}
            AND b.codeGroupName LIKE CONCAT('%',#{shValue},'%')
            AND a.codeSeq = #{shValue}
            AND a.codeName LIKE CONCAT('%',#{shValue},'%')
            AND a.codeMemo LIKE CONCAT('%',#{shValue},'%')

mapper.select:
select aa.* from ( select a.codeSeq ,a.codeDelNy ,a.codeUseNy ,a.codeName ,a.codeMemo ,a.codeRegDate ,a.codeModDate ,a.codeGroup_codeGroupSeq as codeGroupSeqF ,b.codeGroupSeq ,b.codeGroupName
FROM code a left join codeGroup b on a.codeGroup_codeGroupSeq = b.codeGroupSeq and a.codeDelNy = 0 where 1=1
AND a.codeDelNy = #{shDelNy}
AND a.codeRegDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.codeModDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND b.codeGroupSeq = #{shValue} AND b.codeGroupName LIKE CONCAT('%',#{shValue},'%') AND a.codeSeq = #{shValue} AND a.codeName LIKE CONCAT('%',#{shValue},'%') AND a.codeMemo LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.codeGroup_codeGroupSeq desc, a.codeSeq
) aa
limit 10 offset 0

select aa.* from ( select a.codeSeq ,a.codeDelNy ,a.codeUseNy ,a.codeName ,a.codeMemo ,a.codeRegDate ,a.codeModDate ,a.codeGroup_codeGroupSeq as codeGroupSeqF ,b.codeGroupSeq ,b.codeGroupName
FROM code a left join codeGroup b on a.codeGroup_codeGroupSeq = b.codeGroupSeq and a.codeDelNy = 0 where 1=1
AND a.codeDelNy = #{shDelNy}
AND a.codeRegDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.codeModDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND b.codeGroupSeq = #{shValue} AND b.codeGroupName LIKE CONCAT('%',#{shValue},'%') AND a.codeSeq = #{shValue} AND a.codeName LIKE CONCAT('%',#{shValue},'%') AND a.codeMemo LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.codeGroup_codeGroupSeq desc, a.codeSeq
) aa
limit 5 offset 0

		select COUNT(*)

		FROM code a
		left join codeGroup b 
			on a.codeGroup_codeGroupSeq = b.codeGroupSeq 
				and a.codeDelNy = 0
		where 1=1


		AND a.codeDelNy = #{shDelNy}


			AND a.codeRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.codeModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND b.codeGroupSeq = #{shValue}
            AND b.codeGroupName LIKE CONCAT('%',#{shValue},'%')
            AND a.codeSeq = #{shValue}
            AND a.codeName LIKE CONCAT('%',#{shValue},'%')
            AND a.codeMemo LIKE CONCAT('%',#{shValue},'%')

mapper.update:
update code
set
	codeName = #{codeName}
	,codeMemo = #{codeMemo}
	,codeModDate = now()
	,codeGroup_codeGroupSeq = #{codeGroupSeqF}
where 1=1
	and codeSeq = #{codeSeq}

mapper.delete:
delete from code

where 1=1
	and codeSeq = #{codeSeq}
