mapper.select:
select a.productSeq ,a.productDelNy ,a.productUseNy ,a.productBestNy ,a.productNewNy ,a.productName ,a.productInfo ,a.productStock ,a.productPrice ,a.productCatMainCd ,a.productCatDetailCd ,a.productStarAll ,a.productSizeCd ,a.productColorCd ,a.productRegDate ,a.productModDate ,b.codeSeq ,b.codeName from product a left join code b on a.productCatDetailCd = b.codeSeq and a.productDelNy = 0 where 1=1 and a.productDelNy = 0
AND a.productDelNy = #{shDelNy} AND a.productBestNy = #{productBestNy} AND a.productNewNy = #{productNewNy}
AND a.productRegDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.productModDate BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.productSeq = #{shValue} AND a.codeName LIKE CONCAT('%',#{shValue},'%') AND a.productName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
update product
set
	productName = #{productName}
	,productInfo = #{productInfo}
	,productStock = #{productStock}
	,productBestNy = #{productBestNy}
	,productNewNy = #{productNewNy}
	,productInfo = #{productInfo}
	,productModDate = now()
	,productPrice = #{productPrice}
	,productCatMainCd = #{productCatMainCd}
	,productCatDetailCd = #{productCatDetailCd}
	,productSizeCd = #{productSizeCd}
	,productColorCd = #{productColorCd}
where 1=1
	and productSeq = #{productSeq}

mapper.delete:
delete from product

where 1=1
	and productSeq = #{productSeq}
