mapper.update:
update productorders
set
	productOrdersCount = #{productOrdersCount}
	,product_productSeq = #{productSeq}
	,codeModDate = now()
	,orders_ordersSeq = #{ordersSeq}
where 1=1
	and productOrdersSeq = #{productOrdersSeq}

mapper.delete:
delete from productorders

where 1=1
	and productOrdersSeq = #{productOrdersSeq}
